$(document).ready(function(){$('.hide_bomb').fadeOut(6000);$("#content, #footer, #social-networks").stop().animate({"opacity":"1"},3000);$(".fade_jquery").hover(function(){$(this).stop().animate({"opacity":"1"},"slow")},function(){$(this).stop().animate({"opacity":"0.5"},"slow")});$('.error,.success').css('margin-left','-1000px');$('.error,.success').animate({marginLeft:'+=1000px',},2000,function(){})});$(".hide_this").click(function(){$(this).slideUp(2000)});$(function(){$('.slidedown').slideDown(1500)});function hide_intro(){$('#intro').slideUp(2000);$.ajax({type:'POST',url:'/ajax/hide_intro.php'});return false};

$(document).ready(function(){
	$("#submit_fact_admin").submit( function(){
		$('#notification').html('');
		$('#notification').html('<div class="error">Loading..</div>').slideDown();
		textarea_text_fact = $('#textarea_text_fact').val();

		$.post("/ajax/add_fact.php", { text_fact: textarea_text_fact, admin: true },
			function(data)
			{
				if(data == 1)
				{
					var div_response = '<div class="success">Your Fact has been added successfully!</div>';
				}
				else
				{
					var div_response = '<div class="error">Your Fact could not be added.</div>';
				}

				$('#notification').html(div_response).delay(1000).slideUp();
				$('#textarea_text_fact').val('');
				$('#textarea_text_fact').focus();
			});
		return false;
			});
		
});